mapper.select:
select a.AREA_NAME area_name, a.area_code area_code, nvl(t.yjs, 0) yjs, nvl(t.yellow, 0) yellow, nvl(t.red, 0) red, nvl(t.cancel_yellow_num, 0) cancel_yellow_num, nvl(t.cancel_red_num, 0) cancel_red_num from v_ta_dic_area a left join ( select o.County_CODE, sum(ssi.supervise_green_num) as yjs, sum(ssi.supervise_yello_num) as yellow, sum(ssi.supervise_red_num) as red, sum(ssi.cancel_yellow_num) as cancel_yellow_num, sum(ssi.cancel_red_num) as cancel_red_num from ta_jc_sum_supervise_info ssi, v_td_sm_organization1 o where ssi.org_id = o.ORG_ID and ssi.now_date >= #{start_time} and ssi.now_date <= #{end_time} and ssi.city_code = #{ORG_ID,jdbcType=VARCHAR} and ssi.county_code != '000000000000' group by o.County_CODE ) t on a.AREA_CODE = t.County_CODE where a.parent_code = #{ORG_ID,jdbcType=VARCHAR}
order by ${sortCol} desc
order by a.ORDER_NUM

WITH TEMP AS( SELECT * FROM V_ORG V where 1=1
and V.CODE LIKE CONCAT(#{ORG_ID},'%') OR V.ID = #{ORG_ID,jdbcType=VARCHAR}
), TEMP2 AS( select o.ORG_ID, o.ORG_NAME area_name, nvl(t.yjs, 0) yjs, nvl(t.yellow, 0) yellow, nvl(t.red, 0) red, nvl(t.cancel_yellow_num, 0) cancel_yellow_num, nvl(t.cancel_red_num, 0) cancel_red_num from V_TD_SM_ORGANIZATION1 o left join (select ssi.org_id, sum(ssi.supervise_green_num) as yjs, sum(ssi.supervise_yello_num) as yellow, sum(ssi.supervise_red_num) as red, sum(ssi.cancel_yellow_num) as cancel_yellow_num, sum(ssi.cancel_red_num) as cancel_red_num from ta_JC_sum_supervise_info ssi where 1=1 and ssi.now_date >= #{start_time} and ssi.now_date <= #{end_time} group by ssi.org_id) t on o.ORG_ID = t.org_id INNER JOIN TEMP ON o.org_id = TEMP.ID order by o.ORDER_NUM ) select * from TEMP2
