	c2_temp_table ON VA_TA_JC_SUPERVISE_INFO.instance_id = c2_temp_table.instance_id

with orgCondition as (
SELECT v.id,v.code FROM V_ORG V where V.CODE LIKE CONCAT(#{orgId},'%') OR V.ID = #{orgId,jdbcType=VARCHAR}
select v1.id,v1.code from V_ORG V1 START WITH V1.ID=#{temporgid} CONNECT BY V1.PID = PRIOR V1.ID
) select * from ( select from VA_TA_JC_SUPERVISE_INFO left join TA_JC_DIC_SUPERVISE_RESULT on VA_TA_JC_SUPERVISE_INFO.supervise_result_no = TA_JC_DIC_SUPERVISE_RESULT.Supervise_Result_Code left join TA_JC_DIC_SUPERVISE_TYPE on VA_TA_JC_SUPERVISE_INFO.supervise_type_code=TA_JC_DIC_SUPERVISE_TYPE.Supervise_Type_Code inner join orgCondition a on VA_TA_JC_SUPERVISE_INFO.org_id=a.id
) TEMP2 where 1=1
order by accept_time DESC,instance_code DESC
