select * from ( select from VA_TA_JC_CONSULT_INFO left join TA_JC_DIC_SUPERVISE_RESULT on VA_TA_JC_CONSULT_INFO.supervise_result_no=TA_JC_DIC_SUPERVISE_RESULT.Supervise_Result_Code left join TA_JC_DIC_SUPERVISE_TYPE on VA_TA_JC_CONSULT_INFO.supervise_type_code=TA_JC_DIC_SUPERVISE_TYPE.Supervise_Type_Code where exists ( select id from (
SELECT * FROM V_ORG V
V.CODE LIKE CONCAT(#{orgId},'%') OR V.ID = #{orgId,jdbcType=VARCHAR}
select * from V_ORG V1 START WITH V1.ID=#{tempOrgId} CONNECT BY V1.PID = PRIOR V1.ID
) temp where VA_TA_JC_CONSULT_INFO.ORG_ID=temp.id ) UNION select from VA_TA_JC_CONSULT_INFO left join TA_JC_DIC_SUPERVISE_RESULT on VA_TA_JC_CONSULT_INFO.supervise_result_no=TA_JC_DIC_SUPERVISE_RESULT.Supervise_Result_Code left join TA_JC_DIC_SUPERVISE_TYPE on VA_TA_JC_CONSULT_INFO.supervise_type_code=TA_JC_DIC_SUPERVISE_TYPE.Supervise_Type_Code where exists ( select id from (
SELECT * FROM V_ORG V
V.CODE LIKE CONCAT(#{orgId},'%') OR V.ID = #{orgId,jdbcType=VARCHAR}
select * from V_ORG V1 START WITH V1.ID=#{tempOrgId} CONNECT BY V1.PID = PRIOR V1.ID
) temp where VA_TA_JC_CONSULT_INFO.ORG_ID=temp.id ) ) TEMP2
order by TEMP2.consult_time desc
